#Title of Assignment"PyBank Homework Solution"#Import dependencies/packagesimport osimport csv#Files to load and outputfile_to_load = os.path.join("Resources, budget_data.csv")file_to_output = os.path.join("Analysis, budget_data_reformatted.csv")#Create lists for storing dataprofit = []monthly_changes = []date = []#Initialize variablescount = 0total_profit = 0total_change_profit = 0initial_profit = 0#Open CSVwith open(file_to_load, newline="") as csvfile:    csvreader = csv.reader(csvfile, delimiter=",")    csv_header = next(csvreader)    #Cycle through rows of data    for row in csvreader:               #Update count for new month        count = count + 1                #Store value for greatest profit/loss        date.append(row[0])                #Update profit and calculate total profit        profit.append(row[1])        total_profit = total_profit + int(row[1])               #Calculate average monthly change in profit        final_profit = int(row[1])        monthly_change_profit = final_profit - initial_profit                #Store monthly changes        monthly_changes.append(monthly_change_profit)        total_change_profit = total_change_profit + monthly_change_profit        initial_profit = final_profit                #Calculate average profit change        average_change_profit = (total_change_profit/count)                #Find greatest monthly profit and largest monthly loss        greatest_profit = max(monthly_changes)        largest_loss = min(monthly_changes)                profit_date = date[monthly_changes.index(greatest_profit)]        loss_date = date[monthly_changes.index(largest_loss)]                        