#Title of Assignment"PyBank Homework Solution"#Import dependencies/packagesimport osimport csv#Load CSV filefile_to_load = os.path.join("Resources", "budget_data.csv")#Create lists for storing dataprofit = []monthly_changes = []date = []#Open CSVwith open(file_to_load, newline="") as csvfile:    csvreader = csv.reader(csvfile, delimiter=",")    csv_header = next(csvreader)    #Cycle through rows of data    for row in csvreader:               #Update month count and total profit        profit.append(int(row[1]))        date.append(row[0])        #Iterate through profits to find monthly profit change       for i in range(len(profit)-1):                #Take difference between two months to append monthly profit change        monthly_changes.append(profit[i+1] - profit[i])#Calculate average profit changeaverage_change_profit = round(sum(monthly_changes)/len(monthly_changes), 2)        #Find greatest monthly profit and largest monthly lossgreatest_profit = max(monthly_changes)largest_loss = min(monthly_changes)profit_date = date[monthly_changes.index(greatest_profit)]loss_date = date[monthly_changes.index(largest_loss)]#Print statementsprint("-------------------------------------------")print("Financial Analysis")print("-------------------------------------------")print("Total Months: " + str(len(date)))print("Net Total, Profits/Losses: $" + str(sum(profit)))print("Average Monthly Change: $" + str(average_change_profit))print("Greatest Profit Increase: $" + str(greatest_profit) + " (" + str(profit_date) + ")")print("Largest Loss: $" + str(largest_loss) + " (" + str(loss_date) + ")")print("-------------------------------------------")#Push statements to output fileoutput = os.path.join("Analysis", "Financial_Analysis_Summary.txt")with open(output, "w") as file:    file.write("Financial Analysis")    file.write("\n")    file.write("--------------------------------------")    file.write("\n")    file.write("Total Months: " + str(len(date)))    file.write("\n")    file.write("Net Total, Profits/Losses: $" + str(sum(profit)))    file.write("\n")    file.write("Average Monthly Change: $" + str(average_change_profit))    file.write("\n")    file.write("Greatest Profit Increase: $" + str(greatest_profit) + " (" + str(profit_date) + ")")    file.write("\n")    file.write("Largest Loss: $" + str(largest_loss) + " (" + str(loss_date) + ")")                