#Title of Assignment"PyBank Homework Solution"#Import dependencies/packagesimport osimport csv#Load CSV filefile_to_load = os.path.join("Resources", "budget_data.csv")#Create lists for storing dataprofit = []monthly_changes = []date = []#Initialize variablescount = 0total_change_profit = 0initial_profit = 0#Open CSVwith open(file_to_load, newline="") as csvfile:    csvreader = csv.reader(csvfile, delimiter=",")    csv_header = next(csvreader)    #Cycle through rows of data    for row in csvreader:               #Update count for new month        count = count + 1          #Update month count and total profit        profit.append(int(row[1]))        date.append(row[0])               #Calculate average monthly change in profit        final_profit = int(row[1])        monthly_change_profit = final_profit - initial_profit                #Store monthly changes        monthly_changes.append(monthly_change_profit)        initial_profit = final_profit                #Calculate average profit change        average_change_profit = round(sum(monthly_changes)/len(monthly_changes), 2)                #Find greatest monthly profit and largest monthly loss        greatest_profit = max(monthly_changes)        largest_loss = min(monthly_changes)        profit_date = date[monthly_changes.index(greatest_profit)]        loss_date = date[monthly_changes.index(largest_loss)]print("-------------------------------------------")print("Financial Analysis")print("-------------------------------------------")print("Total Months: " + str(count))print("Net Total, Profits/Losses: $" + str(sum(profit)))print("Average Monthly Change: $" + str(average_change_profit))print("Greatest Profit Increase: $" + str(greatest_profit) + " (" + str(profit_date) + ")")print("Largest Loss: $" + str(largest_loss) + " (" + str(loss_date) + ")")print("-------------------------------------------")                