#Title of Assignment"PyPoll Homework Solution"#Import dependenciesimport osimport csv#Load CSV filefile_to_load = os.path.join("Resources", "election_data.csv")#Initialize variablestotal_votes = 0khan_votes = 0correy_votes = 0li_votes = 0otooley_votes = 0#Open CSVwith open(file_to_load, newline="") as csvfile:    csvreader = csv.reader(csvfile, delimiter=",")    csv_header = next(csvreader)    #Cycle through rows of data    for row in csvreader:                #Update total vote count        total_votes += 1                #Update candidate count based on vote        if row[2] == "Khan":            khan_votes += 1                    elif row[2] == "Correy":            correy_votes += 1                    elif row[2] == "Li":            li_votes += 1                    elif row[2] == "O'Tooley":            otooley_votes += 1#Find candidates' percentage of vote wonkhan_percent = (khan_votes/total_votes)*100correy_percent = (correy_votes/total_votes)*100li_percent = (li_votes/total_votes)*100otooley_percent = (otooley_votes/total_votes)*100#Find winner by creating dictionaries of lists and zipping themcandidates = ["Khan", "Correy", "Li", "O'Tooley"]votes = [khan_votes, correy_votes, li_votes, otooley_votes]candidates_and_votes = dict(zip(candidates, votes))key = max(candidates_and_votes, key = candidates_and_votes.get)#Print resultsprint("Election Results")print("------------------------------")print("Total Votes: " + str(total_votes))print("------------------------------")print(f"Khan: {khan_percent:.3f}% ({khan_votes})")print(f"Correy: {correy_percent:.3f}% ({correy_votes})")print(f"Li: {li_percent:.3f}% ({li_votes})")print(f"O'Tooley: {otooley_percent:.3f}% ({otooley_votes})")print("------------------------------")print("Winner: " + str(key))print("------------------------------")